    format      ascii;
    class       dictionary;
    location    "system";
    object      fvSolution;
}


solvers
{
    alpha.air
    {
        max             0.9;
    }

    p
    {
        solver          GAMG;
        tolerance       1e-06;
        relTol          0.01;
        smoother        GaussSeidel;
    }

    pFinal
    {
        solver          GAMG;
        tolerance       1e-06;
        relTol          0;
        smoother        GaussSeidel;
    }

    "(U|k|omega).*"
    {
        solver          smoothSolver;
        smoother        symGaussSeidel;
        tolerance       1e-05;
        relTol          0.1;
    }

    "(U|k|omega).*Final"
    {
        $U;
        tolerance       1e-05;
        relTol          0;
    }
}

