FoamFile
{
    format      ascii;
    class       dictionary;
    location    "system";
    object      fvSolution;
}


solvers
{
    "cellDisplacement.*"
    {
        solver          GAMG;
        tolerance       1e-5;
        relTol          0;
        smoother        GaussSeidel;
    }

    "pcorr.*"
    {
        solver          GAMG;
        tolerance       1e-2;
        relTol          0;
        smoother        GaussSeidel;
        maxIter         100;
    }

    p_gh
    {
        $pcorr;
        tolerance       1e-7;
        relTol          0.1;
    }

    p_ghFinal
    {
        $p_gh;
        relTol          0;
    }

    U
    {
        solver          smoothSolver;
        smoother        symGaussSeidel;
        tolerance       1e-7;
        relTol          0.1;
    }

    UFinal
    {
        $U;
        relTol          0;
    }
}

